import './App.css';
import axios from 'axios';
function App() {
  //高雄
const BASE1 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E9%AB%98%E9%9B%84%E5%B8%82"
  //嘉義
const BASE2 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E5%98%89%E7%BE%A9%E7%B8%A3"
axios.get(BASE1)
.then((response) => {
  console.log("高雄", response)
  const location =response.data.records.location[0].locationName
  const Wx1Starttime =response.data.records.location[0].weatherElement[0].time[0].startTime;
  const Wx1Endtime =response.data.records.location[0].weatherElement[0].time[0].endTime;
  const Wx1 =response.data.records.location[0].weatherElement[0].time[0].parameter.parameterName;
  const Wx2Starttime =response.data.records.location[0].weatherElement[0].time[1].startTime;
  const Wx2Endtime =response.data.records.location[0].weatherElement[0].time[1].endTime;
  const Wx2 =response.data.records.location[0].weatherElement[0].time[1].parameter.parameterName;

  rendercity(location, Wx1, Wx1Starttime, Wx1Endtime, Wx2, Wx2Starttime, Wx2Endtime);

})
axios.get(BASE2)
.then((response) => {

})

function rendercity(location, Wx1, Wx1Starttime, Wx1Endtime, Wx2, Wx2Starttime, Wx2Endtime) {
const city1 = document.querySelector('[data-target="city1"]')
const time1 = document.querySelector('.time1')
city1.innerHTML = `
<div className="city" data-target="city1">
   <h2> ${location} </h2>
</div>
`
time1.innerHTML = `
 <div className="time1">
 <p> ${formateDate(Wx1Starttime)} ${formateTime(Wx1Starttime)} 至  
 ${formateTime(Wx1Endtime)} ${Wx1} </p>
</div>
 `
}

function formateDate(date) {
  var numeric = { day: 'numeric', month: 'numeric' };
  return new Date(date).toLocaleDateString("zh-Hant", numeric)
}
function formateTime(time) {
  var short = {hour: '2-digit'}
  return new Date(time).toLocaleTimeString("zh-Hant", short)
}

  return (
    <div className="city1" data-target="city1">
        <div className='time1'></div>
        <div className='time2'></div>
    </div>
  );
}

export default App;
