import './App.css';
import axios from 'axios';
import { useState } from 'react';



function App() {
  const [location, setLocation] = useState("");
  const [weather1, setWeather1] = useState("")
  const [weather1Start, setWeather1Start] = useState("")
  const [weather1End, setWeather1End] = useState("")
  const [weather2, setWeather2] = useState("")
  const [weather2Start, setWeather2Start] = useState("")
  const [weather2End, setWeather2End] = useState("")
  const [weather3, setWeather3] = useState("")
  const [weather3Start, setWeather3Start] = useState("")
  const [weather3End, setWeather3End] = useState("")
  const [rain1, setRain1] = useState("")
  //高雄
  const BASE1 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E9%AB%98%E9%9B%84%E5%B8%82"
  //嘉義
  const BASE2 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E5%98%89%E7%BE%A9%E7%B8%A3"
  axios.get(BASE1).then(res => {
    console.log(res)
    const city = res.data.records.location[0].locationName
    const Wx1Starttime = res.data.records.location[0].weatherElement[0].time[0].startTime;
    const Wx1Endtime = res.data.records.location[0].weatherElement[0].time[0].endTime;
    const Wx1 = res.data.records.location[0].weatherElement[0].time[0].parameter.parameterName;
    const Wx2Starttime = res.data.records.location[0].weatherElement[0].time[1].startTime;
    const Wx2Endtime = res.data.records.location[0].weatherElement[0].time[1].endTime;
    const Wx2 = res.data.records.location[0].weatherElement[0].time[1].parameter.parameterName;
    const Wx3Starttime = res.data.records.location[0].weatherElement[0].time[2].startTime;
    const Wx3Endtime = res.data.records.location[0].weatherElement[0].time[2].endTime;
    const Wx3 = res.data.records.location[0].weatherElement[0].time[2].parameter.parameterName;
    const Pop1 = res.data.records.location[0].weatherElement[1].time[0].parameter.parameterName;
    const Pop2 = res.data.records.location[0].weatherElement[1].time[1].parameter.parameterName;
    const Pop3 = res.data.records.location[0].weatherElement[1].time[2].parameter.parameterName;
    setLocation(city)
    setWeather1(Wx1)
    setWeather1Start(Wx1Starttime)
    setWeather1End(Wx1Endtime)
    setWeather2(Wx2)
    setWeather2Start(Wx2Starttime)
    setWeather2End(Wx2Endtime)
    setWeather3(Wx3)
    setWeather3Start(Wx3Starttime)
    setWeather3End(Wx3Endtime)
  })

  function formateDate(date) {
    var numeric = { day: 'numeric', month: 'numeric' };
    return new Date(date).toLocaleDateString("zh-Hant", numeric)
  }
  function formateTime(time) {
    var short = { hour: '2-digit' }
    return new Date(time).toLocaleTimeString("zh-Hant", short)
  }


  return (
    <div className='container'>
      <div className="city1">
        <h2>{location}</h2>
        <div className='time1'>
          <p>{formateDate(weather1Start)} {formateTime(weather1Start)} 至 {formateDate(weather1End)} {formateTime(weather1End)} </p>
          <p>{weather1}</p>
        </div>
        <div className='time2'>
          <p>{formateDate(weather2Start)} {formateTime(weather2Start)} 至 {formateDate(weather2End)} {formateTime(weather2End)} </p>
          <p>{weather2}</p>
        </div>
        <div className='time3'>
        <p>{formateDate(weather3Start)} {formateTime(weather3Start)} 至 {formateDate(weather3End)} {formateTime(weather3End)} </p>
          <p>{weather3}</p>
        </div>
      </div>

    </div>
  );
}

export default App;
