import './App.css';
import axios from 'axios';
import React, { PureComponent } from 'react'

const api = axios.create({
  baseURL: `https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E9%AB%98%E9%9B%84%E5%B8%82`
})

function App() {
  //高雄
  const BASE1 ="https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E9%AB%98%E9%9B%84%E5%B8%82"
   //嘉義
  const BASE2 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E5%98%89%E7%BE%A9%E7%B8%A3"
  axios.get(BASE1).then(res => {
    console.log(res)
    const location = res.data.records.location[0].locationName
    console.log(location)
    this.setState({locations: res})
  }) 

  state = {
    locations:[]
  }

  return (
    <div className='container'>
      <div className="city1">
       {this.state.locations.map(location => <h2>{res.data.records.location[0].locationName}</h2>)}
      </div>
      <div className='time1'>

      </div>
      <div className='time2'>

      </div>
      <div className='time3'>

      </div>
    </div>
  );
}

export default App;
