import './App.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import {cityOption} from "./cityOption"


function App() {
  const [location, setLocation] = useState("");
  const [weather1, setWeather1] = useState("")
  const [weather1Start, setWeather1Start] = useState("")
  const [weather1End, setWeather1End] = useState("")
  const [weather2, setWeather2] = useState("")
  const [weather2Start, setWeather2Start] = useState("")
  const [weather2End, setWeather2End] = useState("")
  const [weather3, setWeather3] = useState("")
  const [weather3Start, setWeather3Start] = useState("")
  const [weather3End, setWeather3End] = useState("")
  const [rain1, setRain1] = useState("")
  const [rain2, setRain2] = useState("")
  const [rain3, setRain3] = useState("")
  const [minT1, setminT1] = useState("")
  const [minT2, setminT2] = useState("")
  const [minT3, setminT3] = useState("")
  const [maxT1, setmaxT1] = useState("")
  const [maxT2, setmaxT2] = useState("")
  const [maxT3, setmaxT3] = useState("")
  var city;
  var Wx1Starttime;
  var Wx1Endtime;
  var Wx1;
  var Wx2Starttime;
  var Wx2Endtime;
  var Wx2;
  var Wx3;
  var Wx3Endtime;
  var Wx3Starttime;
  var Pop1;
  var Pop2;
  var Pop3;
  var min1;
  var min2;
  var min3;
  var max1;
  var max2;
  var max3;
  var J_city;
  var J_Wx1Starttime;
  var J_Wx1Endtime;
  var J_Wx1;
  var J_Wx2Starttime;
  var J_Wx2Endtime;
  var J_Wx2;
  var J_Wx3;
  var J_Wx3Endtime;
  var J_Wx3Starttime;
  var J_Pop1;
  var J_Pop2;
  var J_Pop3;
  var J_min1;
  var J_min2;
  var J_min3;
  var J_max1;
  var J_max2;
  var J_max3;

  //高雄 URL
  const BASE1 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E9%AB%98%E9%9B%84%E5%B8%82"
  //嘉義縣 URL
  const BASE2 = "https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=CWB-F51B3957-C9B6-47C1-820C-1EEFDDDA93E7&format=JSON&locationName=%E5%98%89%E7%BE%A9%E7%B8%A3"
  //台南市

  axios.get(BASE1).then(res => {
    // 高雄
    city = res.data.records.location[0].locationName
    Wx1Starttime = res.data.records.location[0].weatherElement[0].time[0].startTime;
     Wx1Endtime = res.data.records.location[0].weatherElement[0].time[0].endTime;
     Wx1 = res.data.records.location[0].weatherElement[0].time[0].parameter.parameterName;
     Wx2Starttime = res.data.records.location[0].weatherElement[0].time[1].startTime;
     Wx2Endtime = res.data.records.location[0].weatherElement[0].time[1].endTime;
     Wx2 = res.data.records.location[0].weatherElement[0].time[1].parameter.parameterName;
     Wx3Starttime = res.data.records.location[0].weatherElement[0].time[2].startTime;
     Wx3Endtime = res.data.records.location[0].weatherElement[0].time[2].endTime;
     Wx3 = res.data.records.location[0].weatherElement[0].time[2].parameter.parameterName;
     Pop1 = res.data.records.location[0].weatherElement[1].time[0].parameter.parameterName;
     Pop2 = res.data.records.location[0].weatherElement[1].time[1].parameter.parameterName;
     Pop3 = res.data.records.location[0].weatherElement[1].time[2].parameter.parameterName;
     min1 = res.data.records.location[0].weatherElement[2].time[0].parameter.parameterName;
     min2 = res.data.records.location[0].weatherElement[2].time[1].parameter.parameterName;
     min3 = res.data.records.location[0].weatherElement[2].time[2].parameter.parameterName;
     max1 = res.data.records.location[0].weatherElement[4].time[0].parameter.parameterName;
     max2 = res.data.records.location[0].weatherElement[4].time[1].parameter.parameterName;
     max3 = res.data.records.location[0].weatherElement[4].time[2].parameter.parameterName;
   })

   axios.get(BASE2).then(res => {
    // 嘉義縣
    console.log(res)
    J_city = res.data.records.location[0].locationName
    J_Wx1Starttime = res.data.records.location[0].weatherElement[0].time[0].startTime;
    J_Wx1Endtime = res.data.records.location[0].weatherElement[0].time[0].endTime;
    J_Wx1 = res.data.records.location[0].weatherElement[0].time[0].parameter.parameterName;
    J_Wx2Starttime = res.data.records.location[0].weatherElement[0].time[1].startTime;
    J_Wx2Endtime = res.data.records.location[0].weatherElement[0].time[1].endTime;
    J_Wx2 = res.data.records.location[0].weatherElement[0].time[1].parameter.parameterName;
    J_Wx3Starttime = res.data.records.location[0].weatherElement[0].time[2].startTime;
    J_Wx3Endtime = res.data.records.location[0].weatherElement[0].time[2].endTime;
    J_Wx3 = res.data.records.location[0].weatherElement[0].time[2].parameter.parameterName;
    J_Pop1 = res.data.records.location[0].weatherElement[1].time[0].parameter.parameterName;
    J_Pop2 = res.data.records.location[0].weatherElement[1].time[1].parameter.parameterName;
    J_Pop3 = res.data.records.location[0].weatherElement[1].time[2].parameter.parameterName;
    J_min1 = res.data.records.location[0].weatherElement[2].time[0].parameter.parameterName;
    J_min2 = res.data.records.location[0].weatherElement[2].time[1].parameter.parameterName;
    J_min3 = res.data.records.location[0].weatherElement[2].time[2].parameter.parameterName;
    J_max1 = res.data.records.location[0].weatherElement[4].time[0].parameter.parameterName;
    J_max2 = res.data.records.location[0].weatherElement[4].time[1].parameter.parameterName;
    J_max3 = res.data.records.location[0].weatherElement[4].time[2].parameter.parameterName;
   })


   function printValue(e) {
    const chosen = document.querySelector('.dropdown').value
    if (chosen == "高雄市") {
      setLocation(city)
      setWeather1(Wx1)
      setWeather1Start(Wx1Starttime)
      setWeather1End(Wx1Endtime)
      setWeather2(Wx2)
      setWeather2Start(Wx2Starttime)
      setWeather2End(Wx2Endtime)
      setWeather3(Wx3)
      setWeather3Start(Wx3Starttime)
      setWeather3End(Wx3Endtime)
      setRain1(Pop1)
      setRain2(Pop2)
      setRain3(Pop3)
      setminT1(min1)
      setminT2(min2)
      setminT3(min3)
      setmaxT1(max1)
      setmaxT2(max2)
      setmaxT3(max3)
    } else if (chosen == "嘉義線") {
      setLocation(J_city)
      setWeather1(J_Wx1)
      setWeather1Start(J_Wx1Starttime)
      setWeather1End(J_Wx1Endtime)
      setWeather2(J_Wx2)
      setWeather2Start(J_Wx2Starttime)
      setWeather2End(J_Wx2Endtime)
      setWeather3(J_Wx3)
      setWeather3Start(J_Wx3Starttime)
      setWeather3End(J_Wx3Endtime)
      setRain1(J_Pop1)
      setRain2(J_Pop2)
      setRain3(J_Pop3)
      setminT1(J_min1)
      setminT2(J_min2)
      setminT3(J_min3)
      setmaxT1(J_max1)
      setmaxT2(J_max2)
      setmaxT3(J_max3)
    }
  }


  function formateDate(date) {
    var numeric = { day: 'numeric', month: 'numeric' };
    return new Date(date).toLocaleDateString("zh-Hant", numeric)
  }
  function formateTime(time) {
    var short = { hour: '2-digit' }
    return new Date(time).toLocaleTimeString("zh-Hant", short)
  }





  return (
    <div className='container'>

      <div className="city1">
      <select className='dropdown' onChange={printValue}>
        {cityOption.map(item => {
          return <option key={item.id} className="cityOption" value={item.title}>{item.title}</option>
        })}
      </select>
        <h2 id='chosenLoc'>{location}</h2>
        <div className='time1'>
          <p>{formateDate(weather1Start)} {formateTime(weather1Start)} <br></br>至  <br></br>{formateDate(weather1End)} {formateTime(weather1End)} </p>
          <p>{weather1}</p>
          <p>降雨機率 {rain1} %</p>
          <p>{minT1}&#176; ~ {maxT1}&#176;</p>
        </div>
